-- 更新interview_questions表题库的SQL脚本
-- 基于现有表结构：(category_id, stage_id, question_text, expected_answer, answer_suggestion, difficulty_level, time_limit)
-- 您可以根据需要修改具体的面试题内容

-- 删除之前stage_id=1和5的题目
-- 首先删除相关的practice_sessions记录以避免外键约束错误
DELETE FROM practice_sessions WHERE question_id IN (SELECT id FROM interview_questions WHERE stage_id = 3);
-- 然后删除interview_questions记录
DELETE FROM interview_questions WHERE stage_id = 1;

-- 清空现有题目（可选，如果需要完全替换）
-- TRUNCATE TABLE interview_questions RESTART IDENTITY CASCADE;

-- 验证插入结果
SELECT 
    stage_id,
    category_id,
    COUNT(*) as question_count
FROM interview_questions 
GROUP BY stage_id, category_id 
ORDER BY stage_id, category_id;

-- 查看总题目数量
SELECT COUNT(*) as total_questions FROM interview_questions;